mapper.select:
select p.p_num,p.i_num,p.p_name,p.p_material,p.p_category,p.p_sub_category,p.p_price,p.p_stock, p.p_temperature,p.p_stock,p.p_sales_volume,p.p_capacity,p.p_country,p.p_color,p.p_grade,p.p_state,p.p_date, i.image1,i.image2,i.image3,i.image4,i.image5 from(select /*+ index_desc(product pk_product)*/ rownum rn, p.p_num,p.i_num,p.p_name,p.p_material,p.p_category,p.p_sub_category,p.p_price,p.p_stock, p.p_temperature,p.p_stock,p.p_sales_volume,p.p_capacity,p.p_country,p.p_color,p.p_grade,p.p_state,p.p_date, i.image1,i.image2,i.image3,i.image4,i.image5 from product p, product_image i where p.i_num = i.i_num and p_category = #{p_category} and rownum <= #{pageNum} * #{amount}
order by p_sales_volume desc
order by p_grade desc
order by p_date desc
) x, product p,product_image i where x.p_num = p.p_num and p.i_num = i.i_num and rn > (#{pageNum} - 1) * #{amount}
